from .base import Basefrom django.db import models#Proceso de contratacionclass Pcontratacion(Base):    fech_prco=models.DateField    ('Fecha')    nome_prco=models.CharField    ('Nombre',    max_length=20, null=True, blank=True, default='')    tipo_prco=models.CharField    ('Tipo',    max_length=20, null=True, blank=True, default='')    desc_prco=models.TextField    ('Descripcion',    max_length=1, null=True, blank=True, default='')    codi_coma = models.ForeignKey(        'Cmarco',        on_delete=models.CASCADE,        related_name='Cmarco',    )    class Meta:        ordering = ['fech_prco']        indexes  = [models.Index(fields=['id',])]         db_table = u'"comun\".\"pcontratacion"'