from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Agenciafrom asiam.serializers import AgenciaSerializerfrom asiam.paginations import SmallResultsSetPaginationclass AgenciaListView(generics.ListAPIView):    serializer_class = AgenciaSerializer    permission_classes = ()    queryset = Agencia.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class AgenciaCreateView(generics.CreateAPIView):    serializer_class = AgenciaSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class AgenciaRetrieveView(generics.RetrieveAPIView):    serializer_class = AgenciaSerializer    permission_classes = ()    queryset = Agencia.objects.all()    lookup_field = 'id'class AgenciaUpdateView(generics.UpdateAPIView):    serializer_class = AgenciaSerializer    permission_classes = ()    queryset = Agencia.objects.all()    lookup_field = 'id'    class AgenciaDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Agencia.objects.all()    lookup_field = 'id'