from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Articulofrom asiam.serializers import ArticuloSerializerfrom asiam.paginations import SmallResultsSetPaginationclass ArticuloListView(generics.ListAPIView):    serializer_class = ArticuloSerializer    permission_classes = ()    queryset = Articulo.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class ArticuloCreateView(generics.CreateAPIView):    serializer_class = ArticuloSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class ArticuloRetrieveView(generics.RetrieveAPIView):    serializer_class = ArticuloSerializer    permission_classes = ()    queryset = Articulo.objects.all()    lookup_field = 'id'class ArticuloUpdateView(generics.UpdateAPIView):    serializer_class = ArticuloSerializer    permission_classes = ()    queryset = Articulo.objects.all()    lookup_field = 'id'    class ArticuloDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Articulo.objects.all()    lookup_field = 'id'