from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Bancofrom asiam.serializers import BancoSerializerfrom asiam.paginations import SmallResultsSetPaginationclass BancoListView(generics.ListAPIView):    serializer_class = BancoSerializer    permission_classes = ()    queryset = Banco.objects.all()    #pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class BancoCreateView(generics.CreateAPIView):    serializer_class = BancoSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class BancoRetrieveView(generics.RetrieveAPIView):    serializer_class = BancoSerializer    permission_classes = ()    queryset = Banco.objects.all()    lookup_field = 'id'class BancoUpdateView(generics.UpdateAPIView):    serializer_class = BancoSerializer    permission_classes = ()    queryset = Banco.objects.all()    lookup_field = 'id'    class BancoDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Banco.objects.all()    lookup_field = 'id'