from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Cmarcofrom asiam.serializers import CmarcoSerializerfrom asiam.paginations import SmallResultsSetPaginationclass CmarcoListView(generics.ListAPIView):    serializer_class = CmarcoSerializer    permission_classes = ()    queryset = Cmarco.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class CmarcoCreateView(generics.CreateAPIView):    serializer_class = CmarcoSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class CmarcoRetrieveView(generics.RetrieveAPIView):    serializer_class = CmarcoSerializer    permission_classes = ()    queryset = Cmarco.objects.all()    lookup_field = 'id'class CmarcoUpdateView(generics.UpdateAPIView):    serializer_class = CmarcoSerializer    permission_classes = ()    queryset = Cmarco.objects.all()    lookup_field = 'id'    class CmarcoDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Cmarco.objects.all()    lookup_field = 'id'