from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Cpagofrom asiam.serializers import CpagoSerializerfrom asiam.paginations import SmallResultsSetPaginationclass CpagoListView(generics.ListAPIView):    serializer_class = CpagoSerializer    permission_classes = ()    queryset = Cpago.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class CpagoCreateView(generics.CreateAPIView):    serializer_class = CpagoSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class CpagoRetrieveView(generics.RetrieveAPIView):    serializer_class = CpagoSerializer    permission_classes = ()    queryset = Cpago.objects.all()    lookup_field = 'id'class CpagoUpdateView(generics.UpdateAPIView):    serializer_class = CpagoSerializer    permission_classes = ()    queryset = Cpago.objects.all()    lookup_field = 'id'    class CpagoDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Cpago.objects.all()    lookup_field = 'id'