from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Cuentafrom asiam.serializers import CuentaSerializerfrom asiam.paginations import SmallResultsSetPaginationclass CuentaListView(generics.ListAPIView):    serializer_class = CuentaSerializer    permission_classes = ()    queryset = Cuenta.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class CuentaCreateView(generics.CreateAPIView):    serializer_class = CuentaSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class CuentaRetrieveView(generics.RetrieveAPIView):    serializer_class = CuentaSerializer    permission_classes = ()    queryset = Cuenta.objects.all()    lookup_field = 'id'class CuentaUpdateView(generics.UpdateAPIView):    serializer_class = CuentaSerializer    permission_classes = ()    queryset = Cuenta.objects.all()    lookup_field = 'id'    class CuentaDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Cuenta.objects.all()    lookup_field = 'id'