from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Fpagofrom asiam.serializers import FpagoSerializerfrom asiam.paginations import SmallResultsSetPaginationclass FpagoListView(generics.ListAPIView):    serializer_class = FpagoSerializer    permission_classes = ()    queryset = Fpago.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class FpagoCreateView(generics.CreateAPIView):    serializer_class = FpagoSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class FpagoRetrieveView(generics.RetrieveAPIView):    serializer_class = FpagoSerializer    permission_classes = ()    queryset = Fpago.objects.all()    lookup_field = 'id'class FpagoUpdateView(generics.UpdateAPIView):    serializer_class = FpagoSerializer    permission_classes = ()    queryset = Fpago.objects.all()    lookup_field = 'id'    class FpagoDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Fpago.objects.all()    lookup_field = 'id'