from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Ginstruccionfrom asiam.serializers import GinstruccionSerializerfrom asiam.paginations import SmallResultsSetPaginationclass GinstruccionListView(generics.ListAPIView):    serializer_class = GinstruccionSerializer    permission_classes = ()    queryset = Ginstruccion.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class GinstruccionCreateView(generics.CreateAPIView):    serializer_class = GinstruccionSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class GinstruccionRetrieveView(generics.RetrieveAPIView):    serializer_class = GinstruccionSerializer    permission_classes = ()    queryset = Ginstruccion.objects.all()    lookup_field = 'id'class GinstruccionUpdateView(generics.UpdateAPIView):    serializer_class = GinstruccionSerializer    permission_classes = ()    queryset = Ginstruccion.objects.all()    lookup_field = 'id'    class GinstruccionDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Ginstruccion.objects.all()    lookup_field = 'id'