from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Juridicafrom asiam.serializers import JuridicaSerializerfrom asiam.paginations import SmallResultsSetPaginationclass JuridicaListView(generics.ListAPIView):    serializer_class = JuridicaSerializer    permission_classes = ()    queryset = Juridica.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class JuridicaCreateView(generics.CreateAPIView):    serializer_class = JuridicaSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class JuridicaRetrieveView(generics.RetrieveAPIView):    serializer_class = JuridicaSerializer    permission_classes = ()    queryset = Juridica.objects.all()    lookup_field = 'id'class JuridicaUpdateView(generics.UpdateAPIView):    serializer_class = JuridicaSerializer    permission_classes = ()    queryset = Juridica.objects.all()    lookup_field = 'id'    class JuridicaDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Juridica.objects.all()    lookup_field = 'id'