from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Monedasfrom asiam.serializers import MonedasSerializerfrom asiam.paginations import SmallResultsSetPaginationclass MonedasListView(generics.ListAPIView):    serializer_class = MonedasSerializer    permission_classes = ()    queryset = Monedas.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class MonedasCreateView(generics.CreateAPIView):    serializer_class = MonedasSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class MonedasRetrieveView(generics.RetrieveAPIView):    serializer_class = MonedasSerializer    permission_classes = ()    queryset = Monedas.objects.all()    lookup_field = 'id'class MonedasUpdateView(generics.UpdateAPIView):    serializer_class = MonedasSerializer    permission_classes = ()    queryset = Monedas.objects.all()    lookup_field = 'id'    class MonedasDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Monedas.objects.all()    lookup_field = 'id'