from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Pcontratacionfrom asiam.serializers import PcontratacionSerializerfrom asiam.paginations import SmallResultsSetPaginationclass PcontratacionListView(generics.ListAPIView):    serializer_class = PcontratacionSerializer    permission_classes = ()    queryset = Pcontratacion.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class PcontratacionCreateView(generics.CreateAPIView):    serializer_class = PcontratacionSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class PcontratacionRetrieveView(generics.RetrieveAPIView):    serializer_class = PcontratacionSerializer    permission_classes = ()    queryset = Pcontratacion.objects.all()    lookup_field = 'id'class PcontratacionUpdateView(generics.UpdateAPIView):    serializer_class = PcontratacionSerializer    permission_classes = ()    queryset = Pcontratacion.objects.all()    lookup_field = 'id'    class PcontratacionDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Pcontratacion.objects.all()    lookup_field = 'id'