from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Presupuestofrom asiam.serializers import PresupuestoSerializerfrom asiam.paginations import SmallResultsSetPaginationclass PresupuestoListView(generics.ListAPIView):    serializer_class = PresupuestoSerializer    permission_classes = ()    queryset = Presupuesto.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class PresupuestoCreateView(generics.CreateAPIView):    serializer_class = PresupuestoSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class PresupuestoRetrieveView(generics.RetrieveAPIView):    serializer_class = PresupuestoSerializer    permission_classes = ()    queryset = Presupuesto.objects.all()    lookup_field = 'id'class PresupuestoUpdateView(generics.UpdateAPIView):    serializer_class = PresupuestoSerializer    permission_classes = ()    queryset = Presupuesto.objects.all()    lookup_field = 'id'    class PresupuestoDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Presupuesto.objects.all()    lookup_field = 'id'