from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Profesionesfrom asiam.serializers import ProfesionesSerializerfrom asiam.paginations import SmallResultsSetPaginationclass ProfesionesListView(generics.ListAPIView):    serializer_class = ProfesionesSerializer    permission_classes = ()    queryset = Profesiones.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class ProfesionesCreateView(generics.CreateAPIView):    serializer_class = ProfesionesSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class ProfesionesRetrieveView(generics.RetrieveAPIView):    serializer_class = ProfesionesSerializer    permission_classes = ()    queryset = Profesiones.objects.all()    lookup_field = 'id'class ProfesionesUpdateView(generics.UpdateAPIView):    serializer_class = ProfesionesSerializer    permission_classes = ()    queryset = Profesiones.objects.all()    lookup_field = 'id'    class ProfesionesDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Profesiones.objects.all()    lookup_field = 'id'