from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Tclientesfrom asiam.serializers import TclientesSerializerfrom asiam.paginations import SmallResultsSetPaginationclass TclientesListView(generics.ListAPIView):    serializer_class = TclientesSerializer    permission_classes = ()    queryset = Tclientes.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class TclientesCreateView(generics.CreateAPIView):    serializer_class = TclientesSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class TclientesRetrieveView(generics.RetrieveAPIView):    serializer_class = TclientesSerializer    permission_classes = ()    queryset = Tclientes.objects.all()    lookup_field = 'id'class TclientesUpdateView(generics.UpdateAPIView):    serializer_class = TclientesSerializer    permission_classes = ()    queryset = Tclientes.objects.all()    lookup_field = 'id'    class TclientesDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Tclientes.objects.all()    lookup_field = 'id'