from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Tcomunicacionesfrom asiam.serializers import TcomunicacionesSerializerfrom asiam.paginations import SmallResultsSetPaginationclass TcomunicacionesListView(generics.ListAPIView):    serializer_class = TcomunicacionesSerializer    permission_classes = ()    queryset = Tcomunicaciones.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class TcomunicacionesCreateView(generics.CreateAPIView):    serializer_class = TcomunicacionesSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class TcomunicacionesRetrieveView(generics.RetrieveAPIView):    serializer_class = TcomunicacionesSerializer    permission_classes = ()    queryset = Tcomunicaciones.objects.all()    lookup_field = 'id'class TcomunicacionesUpdateView(generics.UpdateAPIView):    serializer_class = TcomunicacionesSerializer    permission_classes = ()    queryset = Tcomunicaciones.objects.all()    lookup_field = 'id'    class TcomunicacionesDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Tcomunicaciones.objects.all()    lookup_field = 'id'