from django.shortcuts import renderfrom django.http import HttpResponsefrom django.http.response import JsonResponsefrom django.shortcuts import get_object_or_404from rest_framework import statusfrom rest_framework import filters as dffrom rest_framework import generics, statusfrom rest_framework.response import Responsefrom rest_framework.parsers import JSONParser from rest_framework.permissions import IsAuthenticatedfrom asiam.models import Tesolfrom asiam.serializers import TesolSerializerfrom asiam.paginations import SmallResultsSetPaginationclass TesolListView(generics.ListAPIView):    serializer_class = TesolSerializer    permission_classes = []    queryset = Tesol.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class TesolCreateView(generics.CreateAPIView):    #permission_classes = [IsAuthenticated, ]    permission_classes = []    serializer_class = TesolSerializer    def create(self, request, *args, **kwargs):        serializer = self.get_serializer(data=request.data)        serializer.is_valid(raise_exception=True)        self.perform_create(serializer)        headers = self.get_success_headers(serializer.data)        return Response(serializer.data, status=status.HTTP_201_CREATED, headers=headers)class TesolRetrieveView(generics.RetrieveAPIView):    serializer_class = TesolSerializer    permission_classes = []    queryset = Tesol.objects.all()    lookup_field = 'id'class TesolUpdateView(generics.UpdateAPIView):    serializer_class = TesolSerializer    permission_classes = []    queryset = Tesol.objects.all()    lookup_field = 'id'    class TesolDestroyView(generics.DestroyAPIView):    permission_classes = []    serializer_class = TesolSerializer    queryset = Tesol.objects.all()    lookup_field = 'id'