from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Tretencionesfrom asiam.serializers import TretencionesSerializerfrom asiam.paginations import SmallResultsSetPaginationclass TretencionesListView(generics.ListAPIView):    serializer_class = TretencionesSerializer    permission_classes = ()    queryset = Tretenciones.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class TretencionesCreateView(generics.CreateAPIView):    serializer_class = TretencionesSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class TretencionesRetrieveView(generics.RetrieveAPIView):    serializer_class = TretencionesSerializer    permission_classes = ()    queryset = Tretenciones.objects.all()    lookup_field = 'id'class TretencionesUpdateView(generics.UpdateAPIView):    serializer_class = TretencionesSerializer    permission_classes = ()    queryset = Tretenciones.objects.all()    lookup_field = 'id'    class TretencionesDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Tretenciones.objects.all()    lookup_field = 'id'