from django.shortcuts import renderfrom rest_framework import genericsfrom rest_framework import filters as dffrom rest_framework.permissions import IsAuthenticatedfrom asiam.models import Waletrfrom asiam.serializers import WaletrSerializerfrom asiam.paginations import SmallResultsSetPaginationclass WaletrListView(generics.ListAPIView):    serializer_class = WaletrSerializer    permission_classes = ()    queryset = Waletr.objects.all()    pagination_class = SmallResultsSetPagination    filter_backends = (df.SearchFilter, )    search_fields = ('id', )    ordering_fields = ('id', )class WaletrCreateView(generics.CreateAPIView):    serializer_class = WaletrSerializer    #permission_classes = (IsAuthenticated, )    permission_classes = ()class WaletrRetrieveView(generics.RetrieveAPIView):    serializer_class = WaletrSerializer    permission_classes = ()    queryset = Waletr.objects.all()    lookup_field = 'id'class WaletrUpdateView(generics.UpdateAPIView):    serializer_class = WaletrSerializer    permission_classes = ()    queryset = Waletr.objects.all()    lookup_field = 'id'    class WaletrDestroyView(generics.DestroyAPIView):    permission_classes = ()    queryset = Waletr.objects.all()    lookup_field = 'id'